mapper.select:
        SELECT COUNT(*)
		 FROM DSP_ANOMALYDETECT_NOTICE
		WHERE ISUSABLE IS NOT NULL
		AND TID = #{tid}
		AND NOTICE_REASON = #{noticeReason}
		AND ISUSABLE = 'Usable'

mapper.insert:
INSERT INTO DSP_ANOMALYDETECT_NOTICE (
	FACTORYID,
	NOTICEID, 
	NOTICE_DATETIME, 
	NOTICE_UPDATOR, 
	NOTICE_TARGET, 
	NOTICE_REASON, 
	NOTICE_REASONDESCRIPTION
	,TID
) VALUES(
	#{factoryid}
	, #{noticeid}
	, GETDATE()
	, #{noticeUpdator}
    , #{noticeTarget}
    , #{noticeReason}
    , #{noticeReasondescRiption}
    , #{tid}
)
