mapper.select:
        SELECT COUNT(*)
		 FROM DSP_ANOMALYDETECT_OCCUR
		WHERE ISUSABLE IS NOT NULL
		AND OCCURID = #{occurid}
		AND OCCUR_REASON = #{occurReason}
		AND ISUSABLE = 'Usable'

mapper.insert:
INSERT INTO DSP_ANOMALYDETECT_OCCUR (
	FACTORYID,
	OCCURID, 
	OCCUR_DATETIME, 
	OCCUR_UPDATOR, 
	OCCUR_TARGET, 
	OCCUR_REASON, 
	OCCUR_REASONDESCRIPTION, 
	TID
) VALUES(
	#{factoryid}
	, #{occurid}
	, GETDATE()
	, #{occurUpdator}
    , #{occurTarget}
    , #{occurReason}
    , #{occurReasondescRiption}
    , #{tid}
)
