mapper.select:
        SELECT DATASEQ, PLAN_QTY
		 FROM DSP_WORKDAILY_REPORT
		WHERE FACTORYID = #{factoryid}
		AND WORK_DATE = #{workDate}
		AND LINEID = #{lineid}
		AND SHIFTID = #{shiftid}
		AND MATERIALID = #{materialid}
		AND MODELID = #{modelid}
		AND ISUSABLE = 'Usable'

        SELECT COUNT(*)
		 FROM DSP_WORKDAILY_REPORT
		WHERE ISUSABLE IS NOT NULL
		AND WORK_DATE = #{workDate}
		AND FACTORYID = #{factoryid}
		AND LINEID = #{lineid}
		AND SHIFTID = #{shiftid}
		AND MODELID = #{modelid}
		AND MATERIALID = #{materialid}
		AND ISUSABLE = 'Usable'

mapper.insert:
INSERT INTO DSP_WORKDAILY_REPORT (
     FACTORYID
     , WORK_DATE
     , LINEID
     , SHIFTID
     , MODELID
     , MATERIALID
     , REGISTER
     , PLAN_QTY
     , NOTES
     , CREATOR
     , TID
)
VALUES (
     #{factoryid}
     , #{workDate}
     , #{lineid}
     , #{shiftid}
     , #{modelid}
     , #{materialid}
     , #{register}
     , #{planQty}
     , #{notes}
     , #{tid}
)
